import React, { useEffect, useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import { Card, Button } from "react-bootstrap";
import axios from 'axios';

const Test9 = () => {

  const colorSet = ['Light','Danger','Success'];
  const covidData = ['cases','deaths','recover'];

  const [latest, setLatest] = useState("");
  const {TotalConfirmed, TotalDeaths, TotalRecovered} = latest;
  const countryData = [TotalConfirmed,TotalDeaths, TotalRecovered]
  console.log(latest);

  useEffect(()=>{
    axios.get('https://api.covid19api.com/summary')
    .then(res=>{
      setLatest(res.data.Countries[22]);
    })
    .catch(err=>{
      console.log(err);
    }

    )
  },[]);

  return (
    <React.Fragment>
    <div className="d-flex flex-row flex-wrap">
    
      {colorSet.map((variant, idx) => {
        return (
          <Card
            bg={variant.toLowerCase()}
            key={idx}
            text={variant.toLowerCase() === "light" ? "dark" : "white"}
            style={{ width: "18rem" }}
            className="m-2"
          >
            <Card.Header>{covidData[idx]}</Card.Header>
            <Card.Body>
              <Card.Text>
                {countryData[idx]}
              </Card.Text>
            </Card.Body>
          </Card>
        );
      })}
      </div>
    </React.Fragment>
  );
}
export default Test9;
