import React, { useEffect, useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import { Card } from "react-bootstrap";
import axios from 'axios';
import Typography from '@mui/material/Typography';
import TextField from '@mui/material/TextField';

const Test9 = () => {


  
  const [country, setCountry] = useState("");
  const findCountry=(e)=>{
    const {value} = e.target;
    console.log(value);
    setCountry(value);
  }

  const [latest, setLatest] = useState([]);

  const colorSet = ['Light','Danger','Success'];
  const covidData = ['cases','deaths','recover'];

  const searchResult = latest.filter((val,index)=>{
    return val.Country===country;
  }) 
  console.log(searchResult.Country);


  //useeffect start
  useEffect(()=>{
    axios.get('https://api.covid19api.com/summary')
    .then(res=>{
      setLatest(res.data.Countries);
      console.log(res.data.Countries);
    })
    .catch(err=>{
      console.log(err);
    }
    )
  },[]);



  return (
    <React.Fragment>
      <TextField className="mt-5" id="standard-basic" label="Search Country" variant="standard" onChange={findCountry}/>
     <Typography className="m-2" variant="overline" display="block" gutterBottom>
         {country}
      </Typography>
    <div className="d-flex flex-row flex-wrap">
    
      {colorSet.map((variant, idx) => {
        return (
          <Card
            bg={variant.toLowerCase()}
            key={idx}
            text={variant.toLowerCase() === "light" ? "dark" : "white"}
            style={{ width: "18rem" }}
            className="m-2"
          >
            <Card.Header>{covidData[idx]}</Card.Header>
            <Card.Body>
              <Card.Text>
                {searchResult.TotalConfirmed}
              </Card.Text>
            </Card.Body>
          </Card>
        );
      })}
      </div>
    </React.Fragment>
  );
}
export default Test9;
